<?php

namespace UR\DB\NewBundle\Entity;

use Doctrine\ORM\EntityRepository;
use UR\DB\NewBundle\Entity\Date;
/**
 * BaptismRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BaptismRepository extends EntityRepository
{
   
    public function addBaptismDate($baptismObj, $dateId){
        $currentDateString = $baptismObj->getBaptismDateId();

        //expected input: id1,id5,id6
        $dateIdArray = explode(",", $currentDateString);

        foreach($dateIdArray as $currdateId){
        	if($dateId == trim($currdateId)){
        		//date in string ==> no work to do
        		return;
        	}
        }

        //date not in string
        $currentDateString .= ",".$dateId;

        $baptismObj->setBaptismDateId($currentDateString);
    }

    public function removeBaptismDate($baptismObj, $dateId){
        $currentDateString = $baptismObj->getBaptismDateId();

        //expected input: id1,id5,id6
        $dateIdArray = explode(",", $currentDateString);

        $indexFound = -1;

        for($i = 0; $i < count($dateIdArray); $i++){
        	if($dateId == trim($dateIdArray[$i])){
        		//date in string... need to remove it!
        		$indexFound = $i;
        	}
        }

        unset($dateIdArray[$i]);
        $newIdArray = array_values($dateIdArray);

        $newDateString = implode(",", $newIdArray);

        $baptismObj->setBaptismDateId($newDateString);
    }
}